import { React, useState } from 'react';
import {View, TouchableOpacity, Text, TextInput, StyleSheet} from 'react-native';

export default function App(){
  const [n1, setN1] = useState("");
  const [n2, setN2] = useState("");
  const [sum, setSum] = useState("")

  return(
    <View style={styles.container}>
      <TextInput placeholder='1º Número' style={styles.input} value={n1} onChangeText={(val) => { setN1(val);}}/>
      <TextInput style={styles.input} value={n2} onChangeText={(val) => { setN2(val);}}/>
      <TouchableOpacity onPress={() => { setSum(parseInt(n1) + parseInt(n2)) }}>
        <Text>Executar</Text>
      </TouchableOpacity>
      <Text>{sum}</Text>
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center'
  },
  primeira: {
    flex: 1,
    backgroundColor: '#f1de7',
  },
  segunda:{
    flex: 1,
    backgroundColor: '#e2f5c8'
  },
  imgTop: {
    width: '200px',
    height: '200px'
  },
  input: {
    borderBottomWidth: '1px',
    borderBottomColor: 'gray',
    outline: "none",
    marginBottom: '5px'
  },
  btExclui:{
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#FCFFA6',
    border: '2px solid #adffa6',
    borderRadius: '10px'
  }
});
