import { React, useEffect, useState } from 'react';
import {View, TouchableOpacity, Text, TextInput, StyleSheet} from 'react-native';

export default function App(){
  const [n1, setN1] = useState("");
  const [n2, setN2] = useState("");
  const [res, setRes] = useState("");
  const [sumOpacity, setSumOpacity] = useState("1");
  const [minOpacity, setMinOpacity] = useState("1");
  const [divOpacity, setDivOpacity] = useState("1");
  const [multOpacity, setMultOpacity] = useState("1");
  var selected = 0

  function sum() {
    useEffect(()=>{
      switch (selected) {
        case 1:
          setSumOpacity("1")
          selected = 0
          break;
        default:
          setSumOpacity(".5")
          setMinOpacity("1")
          setDivOpacity("1")
          setMultOpacity("1")
          selected = 1
          break;
      }
    })

  }

  function min() {
    switch (selected) {
      case 2:
        setMinOpacity("1")
        selected = 0
        break;
      default:
        setSumOpacity("1")
        setMinOpacity(".5")
        setDivOpacity("1")
        setMultOpacity("1")
        selected = 2
        break;
    }
  }

  function div() {
    switch (selected) {
      case 3:
        setDivOpacity("1")
        selected = 0
        break;
      default:
        setSumOpacity("1")
        setMinOpacity("1")
        setDivOpacity(".5")
        setMultOpacity("1")
        selected = 3
        break;
    }
  }

  function mult() {
    switch (selected) {
      case 4:
        setMultOpacity("1")
        selected = 0
        break;
      default:
        setSumOpacity("1")
        setMinOpacity("1")
        setDivOpacity(".5")
        setMultOpacity("1")
        selected = 4
        break;
    }
  }

  return(
    <View style={styles.container}>
      <TextInput placeholder='1º Número' style={styles.input} value={n1} onChangeText={(val) => { setN1(val);}}/>
      <TextInput placeholder='2º Número' style={styles.input} value={n2} onChangeText={(val) => { setN2(val);}}/>
      <View style={styles.operators}>
        <TouchableOpacity onPress={sum()} style={{...styles.op, opacity: sumOpacity}}><Text style={styles.opText}>+</Text></TouchableOpacity>
        <TouchableOpacity style={{...styles.op, opacity: minOpacity}}><Text style={styles.opText}>-</Text></TouchableOpacity>
        <TouchableOpacity style={{...styles.op, opacity: divOpacity}}><Text style={styles.opText}>÷</Text></TouchableOpacity>
        <TouchableOpacity style={{...styles.op, opacity: multOpacity}}><Text style={styles.opText}>×</Text></TouchableOpacity>
      </View>
      <TouchableOpacity style={{borderColor: 'gray', borderWidth: '1px', padding: '5px', }} onPress={() => { setRes(parseInt(n1) + parseInt(n2)) }}>
        <Text>Executar</Text>
      </TouchableOpacity>
      <Text style={styles.sumLabel}>Resultado:</Text>
      <Text style={{fontSize: 20, marginTop: '10px'}}>{res}</Text>
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center'
  },
  input: {
    borderBottomWidth: '1px',
    borderBottomColor: 'gray',
    outline: "none",
    marginBottom: '5px',
    textAlign: "center"
  },
  sumLabel: {
    marginTop: '20px'
  },
  operators: {
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'space-evenly',
    alignItems: 'center',
    padding: '5px',
    gap: '5px'
  },
  op: {
    borderWidth: '1px',
    borderColor: '#999',
    height: "40px",
    width: "35px",
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'center',
    alignItems: 'center',
    opacity: 1,
  },
  opText: {
    fontSize: '20px',
    fontWeight: 'bold'
  }
});
