import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, Touchable, View } from 'react-native';
import { Button, SafeAreaView, ScrollView, TextInput, TouchableOpacity } from 'react-native-web';
import { useState } from 'react';

export default function Teste() {

  return (
    <View style={styles.container}>
      <Text>Mingau</Text>
      
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: "column",
    alignItems: "center",
    padding: "50px",
    backgroundColor: "#fff",
    width: "100%",
    gap: "10px"
  },
  inpCont: {
    width: "100%",
    gap: "10px"   
  },
  input:{
    borderWidth: "1px",
    borderColor: "#000",
    padding: "10px"
  },
  savCont:{
    flex: 1,
    width: "100%",
    maxHeight: "600px"
  },
  svCont:{
    flex: 1,
    width: "100%",
    backgroundColor: "#ddd",
    padding: "10px"
  },
  func:{
    backgroundColor: "#fff",
    marginVertical: "5px",
    borderRadius: "10px",
    padding: "10px",
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 3,
    },
    shadowOpacity: 0.27,
    shadowRadius: 4.65,

    elevation: 6,
  },
  nome: {
    fontSize: "13pt",
    color: "#1647a8"
  },
  btn: {
    display: "flex",
    borderRadius: "10px",
    backgroundColor: "#1647a8",
    padding: "10px",
    alignItems: "center"
  },
  btnText: {
    color: "#fff"
  }
});
