  ImageBackground,
  StyleSheet,
  Text,
  TouchableOpacity,
  View,
} from "react-native";
import { SafeAreaView, ScrollView } from "react-native-web";
import AsyncStorage from "@react-native-async-storage/async-storage";

export default function Question1({ navigation }) {

  const [score, setScore] = useState(0);

  const getData = async () => {
    try {
      const valor = await AsyncStorage.getItem("score");
      if (jsonValue !== null){
        setScore(valor)
      }
    } catch (e) {
      // error reading value
    }
  };

  return (
    <View style={styles.container}>
      <View style={styles.question}>
        <Text style={styles.questionText}>Teco Teco do Beetlejuice do Oldrikandalahai do meu Saco?</Text>
      </View>
      <View style={styles.options}>

      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#111",
    alignItems: "center",
    padding: 15,
    position: "relative",
    paddingVertical: 200
  },
  question: {
    width: '100%',
    backgroundColor: '#113'
  },
  questionText: {
    fontSize: 20,
    color: '#fff'
  }
});
