    color: '#fff',
    opacity: .5,
    marginTop: 40
  },
  btn: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    paddingHorizontal: 50,
    paddingVertical: 25,
    borderColor: '#58c',
    borderWidth: 2,
    marginTop: 40,
    shadowColor: '#58c',
    shadowOpacity: 1,
    shadowOffset: {
      width: 0,
      height: 0
    },
    shadowRadius: 20
  },
  btnText: {
    fontSize: 40,
    color: '#fff'
  }
});
