        if (value != 9) {
            if(resp[value].right){
                getData()
            }
            navigation.push('Q1')
        }
      }}>
        <Text style={styles.btnText}>CONFIRMAR</Text>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#111",
