import { useState, useEffect } from "react";
import {
  ImageBackground,
  StyleSheet,
  Text,
  TouchableOpacity,
  View,
} from "react-native";
import { SafeAreaView, ScrollView } from "react-native-web";
import AsyncStorage from "@react-native-async-storage/async-storage";

export default function Home({ navigation }) {

  const [score, setScore] = useState(0);

  const getData = async () => {
    try {
      const jsonValue = await AsyncStorage.getItem("cart");
      if (jsonValue !== null){
        setScore()
      }
    } catch (e) {
      // error reading value
    }
  };

  return (
    <View style={styles.container}>
      <View style={styles.title}>
        <Text style={styles.subtext}>VEIO PORQUE</Text>
        <Text style={styles.titleText}>QUIZ</Text>
      </View>
      <Text style={styles.score}>Melhor Pontuação: {score}</Text>
      <TouchableOpacity style={styles.btn}>
        <Text>Começar</Text>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#111",
    alignItems: "center",
    padding: 15,
    position: "relative",
    paddingVertical: 200
  },
  title: {
    display: 'flex',
    alignItems: 'center'
  },
  subtext: {
    fontSize: 24,
    color: '#fff'
  },
  titleText: {
    fontSize: 46,
    color: '#fff'
  },
  score: {
    fontSize: 20,
    color: '#fff',
    opacity: .5
  },
  btn: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    padding: 50,
    borderColor: '#58c',
    borderWidth: 2,
    marginTop: 50
  }
});
